import React from 'react';
import classes from './Cart.module.css';
import CartItem from './CartItem/CartItem';
import Aux from '../../hoc/Aux';
import {BlackButton} from '../UI/UIComponents/Buttons/Buttons';
import {Close} from '@material-ui/icons';

const cartModal = (props) =>{ 
	let cartClass = []
	return (
		<div className={classes.CartModal} >
			<div className={classes.CartTitle} >
				<h1>Your Cart</h1>
				<div className={classes.Close} onClick={props.cartHandler} >
					<Close />
				</div>
			</div>
			{
				props.cart.length ?
				props.cart.map((product, index) =>{
					return <CartItem 
						cartItem={product}
						removeFromCart={props.removeFromCart} />
				}) 
				: <p>Please add products to your cart</p>
			}
			{
				props.cart.length ?	
				<Aux>
					<p>Total Price : 
						<spam className={classes.TotalPrice} >
							{props.totalCartPrice.toFixed(2)}</spam>
					</p>
					<BlackButton>Checkout</BlackButton>
				</Aux>

				: null
			}
		</div>
	);
}

export default cartModal;
