import React from 'react';
import classes from './Drawer.module.css';
import NavItem from '../../NavItem/NavItem';
import {Logo}  from '../../UIComponents/UIComponents';
import {ArrowDropDown} from '@material-ui/icons';

const drawer = (props) => {
	let drawerClass = [classes.Drawer, classes.Close];
	if (props.show)
		drawerClass = [classes.Drawer, classes.Open];

	let categoriesClass = classes.CategoriesDrawerClose;
	if (props.showCat)
		categoriesClass = classes.CategoriesDrawerOpen;
	const style = {
		width: "100%",
		backgroundColor : "white",
 	}
	return (
		<div className={drawerClass.join(' ')} >
			<ul>
				<NavItem><Logo style={{color: "#FFFFFF"}} /></NavItem>
				<NavItem 
					onClick={props.categoriesHandler} 
					className={classes.Categories} >Categories <ArrowDropDown /> </NavItem>
				<NavItem style={style} >
					<ul className={categoriesClass} >
						<NavItem>Sport</NavItem>
						<NavItem>Men</NavItem>
						<NavItem>Women</NavItem>
						<NavItem>Kids</NavItem>
					</ul>
				</NavItem>
				<NavItem>
					<div className={classes.HSeparator} ></div>
				</NavItem>
				<NavItem>Profile</NavItem>
				<NavItem>Cart</NavItem>
			</ul>
		</div>
	);
}

export default drawer;
