import React, {Component} from 'react';
import classes from './Cover.module.css';
import cover1 from '../../../assets/covers/store1.jpg';
import cover2 from '../../../assets/covers/store2.jpg';
import cover3 from '../../../assets/covers/store3.jpg';

class Cover extends Component {
	state = {
		coverImage: cover2
	}

	coverImageHandler = (direction) => {
		if (direction === 1) {
			this.setState({coverImage: cover1})
		}
		if (direction === 2) {
			this.setState({coverImage: cover3})
		}
	}

	render () {
		// let coverImage = cover1;

		return (
			<div 
			className={classes.Cover} 
			style={{backgroundImage: `url(${this.state.coverImage})`}} >
				<button onClick={this.coverImage} >previous</button>
				<button>next</button>
			</div>
		);
	}
}

export default Cover;
