import React, {Component} from 'react';
import classes from './ProductCard.module.css';
import {AddToCartButton} from '../UI/UIComponents/Buttons/Buttons';
import { Loyalty, Visibility, Compare} from '@material-ui/icons';
import BackDrop from '../UI/BackDrop/BackDrop';
import AddToCartModal from '../Selection/AddToCartModal/AddToCartModal';

class productCard extends Component {
	render () {
		return (
	<div className={classes.ProductCard} >
		{props.addToCartModalShow ? <AddToCartModal product={props.product} /> : null}
		{props.addToCartModalShow ? <BackDrop drawerHandler={props.addToCartModelHandler} /> : null}
		<div className={classes.Option}>
			<div><Loyalty /></div>
			<div onClick={props.addToCart} ><Visibility /></div>
			<div><Compare /></div>
		</div>
		<img className={classes.Image} src={props.product.image} alt='' />
		<p className={classes.Name} >{props.product.name}</p>
		<p className={classes.Price} >{props.product.price}</p>
		<AddToCartButton addToCart={props.addToCart} >Add to cart</AddToCartButton>
	</div>)}
}

export default productCard;
