import React, {Component} from 'react';
import Aux from './hoc/Aux';
import Layout from './components/Layout/Layout';
import Cover from './components/UI/Cover/Cover';
import BestFor from './components/BestFor/BestFor';
import Selection from './components/Selection/Selection';
import Coupon from './components/Coupon/Coupon';
import productImg from './assets/product.jpg';
// import BackDrop from './components/UI/BackDrop/BackDrop'
import './App.css';

class App extends Component {
  state = {
    products: [
      {id: '1', name: 'bag1', price: 199.99, image: productImg, description: 'Description1', stock: 100},
      {id: '2', name: 'bag2', price: 299.99, image: productImg, description: 'Description2', stock: 40},
      {id: '3', name: 'bag3', price: 399.99, image: productImg, description: 'Description3', stock: 23},
      {id: '4', name: 'bag4', price: 499.99, image: productImg, description: 'Description4', stock: 1},
      {id: '5', name: 'bag5', price: 599.99, image: productImg, description: 'Description5', stock: 0},
      {id: '6', name: 'bag6', price: 699.99, image: productImg, description: 'Description6', stock: 100},
    ],
    cart: [],
    // showDrawer: false,
  }

  checkIfExistIncart = (newPrd) => {
    this.state.cart.map((product, index) => {
      if (newPrd.id === product.id) {
        return index
      }
    }) 
    return -1
  }
  addToCart = (product, quantity) => {
    const index = checkIfExistIncarts(product)
    console.log(index)
    // if (index > -1) {
    //   this.setState({cart[index].qnt: qnt + quantity}) 
    // }
    this.setState({cart: [...this.state.cart, {prd: product, qnt: quantity}]});
  }

  render() {
    return (
      <Aux>
        <Layout>

          <div onClick={() => this.addToCart(this.state.products[0], 3)} >dada</div>
          <div onClick={() => this.addToCart(this.state.products[2], 5)} >dada</div>
          <div onClick={() => this.addToCart(this.state.products[3], 100)} >dada</div>
          {console.log(this.state.cart)}
          {
            this.state.cart ?
            this.state.cart.map((product, index) =>{
            return <p>[{index}] = {product.prd.name} x {product.qnt}</p>
            }) : null
          }
          {/* <Cover />
          <BestFor /> */}
          <Selection 
            products={this.state.products}
            cart={this.state.cart}
            addToCart={this.addToCart} 
            />
          {/* <Coupon amount="5" /> */}
        </Layout>
      </Aux>
    )
  }
}

export default App;
