import React, {Component} from 'react';
import classes from './Cover.module.css';
import cover1 from '../../../assets/covers/store1.jpg';
import cover2 from '../../../assets/covers/store2.jpg';
import cover3 from '../../../assets/covers/store3.jpg';

class Cover extends Component {
	state = {
		coverImage: [
			{image: cover1},
			{image: cover2},
			{image: cover3}
		],
		currentImage: 0
	}

	coverImageHandler = (direction) => {
		let updatedImage = this.state.currentImage;
		if (direction === 1) {
			if (this.state.currentImage - 1 < 0) {
				updatedImage = this.state.coverImage.length
			}
			else {
				updatedImage = this.state.currentImage - 1;
			}
		}
		if (direction === 2) {
			updatedImage = this.state.currentImage + 1;
		}
		this.setState({currentImage: updatedImage})
	}

	render () {
		// let coverImage = cover1;

		return (
			<div 
			className={classes.Cover} 
			style={{backgroundImage: `url(${this.state.coverImage[this.state.currentImage].image})`}} >
				<button onClick={this.coverImageHandler(1)} >previous</button>
				<button onClick={this.coverImageHandler(2)} >next</button>
				{/* <button>next</button> */}
			</div>
		);
	}
}

export default Cover;
