import React, {Component} from 'react';
import classes from './AddToCartModal.module.css'
import {Close, Add, Remove} from '@material-ui/icons';
import {OutStock} from '../../UI/UIComponents/UIComponents';

class addToCartModal extends Component {
render (){
return (
	<div className={classes.AddToCartModal} >
		<div className={classes.Close} onClick={props.close} >
			<Close />
		</div>
		<div className={classes.ImageDiv} >
			<img className={classes.Image} src={props.product.image} alt='' />
		</div>
		<div className={classes.Content} >
			<p className={classes.Name} >{props.product.name}</p>
			<div className={classes.PriceStock} >
				<p className={classes.Price} >{props.product.price}</p>
				{props.product.stock === 0 ? <OutStock /> :null}
			</div>
			<p className={classes.Description} > {props.product.description} </p>
			<p>Quantity : </p>
			<div className={classes.Options} >
				<div className={classes.Arrow} ><Remove /></div>
				<input type='number'  />
				<div className={classes.Arrow} ><Add /></div>
			</div>
		</div>
	</div>
);
}
}

export default addToCartModal;
