import React, {Component} from 'react';
import Aux from './hoc/Aux';
import Layout from './components/Layout/Layout';
import Cover from './components/UI/Cover/Cover';
import BestFor from './components/BestFor/BestFor';
import Selection from './components/Selection/Selection';
import Coupon from './components/Coupon/Coupon';
import productImg from './assets/product.jpg';
// import BackDrop from './components/UI/BackDrop/BackDrop'
import './App.css';

class App extends Component {
  state = {
    products: [
      {name: 'bag1', price: 199.99, image: productImg, description: 'Description1', stock: 100},
      {name: 'bag2', price: 299.99, image: productImg, description: 'Description2', stock: 40},
      {name: 'bag3', price: 399.99, image: productImg, description: 'Description3', stock: 23},
      {name: 'bag4', price: 499.99, image: productImg, description: 'Description4', stock: 1},
      {name: 'bag5', price: 599.99, image: productImg, description: 'Description5', stock: 0},
      {name: 'bag6', price: 699.99, image: productImg, description: 'Description6', stock: 100},
    ],
    cart: [],
    // showDrawer: false,
  }

  addToCart = (product) => {
    this.setState({cart: [...this.state.cart, product]});
  }

  render() {
    return (
      <Aux>
        <Layout>

          <div onClick={() => this.addToCart(this.state.products[0])} >dada</div>
          <div onClick={() => this.addToCart(this.state.products[2])} >dada</div>
          <div onClick={() => this.addToCart(this.state.products[3])} >dada</div>
          {
            this.state.cart ?
            this.state.cart.map(product =>{
              return <p>{product.name}</p>
            }) : null
          }
          {/* <Cover />
          <BestFor />
          <Selection products={this.state.products} />
          <Coupon amount="5" /> */}
        </Layout>
      </Aux>
    )
  }
}

export default App;
