import React, {Component} from 'react';
import classes from './Selection.module.css';
import ProductCard from '../ProductCard/ProductCard';
import {BlackButton} from '../UI/UIComponents/Buttons/Buttons';
import AddToCartModal from './AddToCartModal/AddToCartModal';
// import BackDrop from '../UI/BackDrop/BackDrop';

const BackDrop = (props) => (
	<div 
	className={classes.BackDrop} 
	onClick={props.drawerHandler}
	></div>
)


class selection extends Component {

	state = {
		addToCartModalShow: false,
	}

	addToCartModelHandler = () => {
		let addToCartModelUpdated = this.state.addToCartModalShow;
		this.setState({addToCartModalShow: !addToCartModelUpdated});
	}

	render () {
		return (
			<div className={classes.Selection} >
				{this.state.addToCartModalShow ? <AddToCartModal /> : null}
				{this.state.addToCartModalShow ? <BackDrop drawerHandler={this.addToCartModelHandler} /> : null}
				<div className={classes.Surround} ></div>
				<div className={classes.Content} >
					<h1>Our finest selection</h1>
					<div className={classes.HSeparator} ></div>
					<div className={classes.ProductList}>
						{this.props.products.map(product => {
							return <ProductCard 
							name={product.name}
							price={product.price}
							image={product.image}
							addToCart={this.addToCartModelHandler}
							/>
						})}
					</div>
					<BlackButton>Show More Products</BlackButton>
				</div>
				<div className={classes.Surround} ></div>
			</div>
		);}
}

export default selection;
